import classes from './Modal.module.css';
import React from 'react';
import ReactDOM from 'react-dom';
/* here we will be using portals:
in the index html add div with id = "overlays" - for portal usage */

const Backdrop = propps => {
return <div className={classes.bachdrop}/>
}

const ModalOverlay = propps => {
return <div className={classes.modal}>
    <div className={classes.content}>{propps.children}</div>
</div>
}

/*Portals receive two arguments: what to portal and where */
const portalElement = document.getElementById('overlays');

const Modal = propps => {
return <React.Fragment>
    {ReactDOM.createPortal(<Backdrop/>, portalElement)}
    {ReactDOM.createPortal(<ModalOverlay>{propps.children}</ModalOverlay>, portalElement)}
</React.Fragment>
}
export default Modal;
