import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';

const MealItemForm = props => {
 /*In the input we are expected ton get lable because in the Input component we retreive it from the props. Syntax for input means
 that we {} - javaScrip expression which contains anotherone {} which is object */   
return <form className={classes.form}>
    <Input lable = "Amount" input = {{
        id: 'amount',
        type: 'number',
        min: '1',
        max: '5',
        step: '1',
        defaultValue: '1'
    }}/>
    <button>+ Add</button>
</form>

}

export default MealItemForm;
